html:
{% set dinar = "{:,.3f}".format(doc.amount).split('.')[0] %}
{% set fills = "{:,.3f}".format(doc.amount).split('.')[1] %}
<!DOCTYPE html>
<html>
<head>
    <style>
        body {
            width: 210mm;
            min-height: 297mm;
            margin: 20px;
            padding: 0;
            font-size: 16px;
            position: relative;
            direction: rtl; 
        }
        p {
            margin: 0;
            padding: 0;
        }
        .text-right {
            text-align: right;
        }
        .text-left {
            text-align: left;
        }
    </style>
</head>
<body>
    <div>
        <big>
            <br><br>
            <p class="text-left" style="position: absolute; right: 142.5mm; top: 3.7mm; font-size: 14px;">
                <b>{{ doc.get_formatted("posting_date") or '' }}</b>
            </p>
    
            <p class="text-left" style="position: absolute; right: 25mm; top: 17mm; font-size: 14px;">
                <b>{{ doc.pay_order_of or ''}}</b>
            </p>
    
    
            <p class="text-left" style="position: absolute; right: 45mm; top: 31mm; font-size: 14px;">
                <b>{{ dinar }}</b>
            </p>
            
            <p class="text-left" style="position: absolute; right: 22.5mm; top: 31mm; font-size: 14px;">
                <b>{{ fills }}</b>
            </p>
        
           <p class="text-right" style="position: absolute; right: 80mm; top: 23.5mm; font-size: 14px;">
                <b>
                    {% set whole_part = doc.amount | int %}
                    {% set decimal_part = ((doc.amount - whole_part) * 1000 ) %}
                
                    {{ frappe.utils.in_words(whole_part) }}&nbsp;&nbsp;دينار أردني
                    {% if decimal_part > 0 %}
                            و {{ fills  }} فلس
                    {% endif %}
                    فقط لا غير
                </b>
            </p>

        </big>
    </div>
</body>
</html>
