css:
@media print {
    body {
        margin: 20mm 15mm 20mm 15mm !important; /* top right bottom left */
        padding: 0 !important;
    }

    .print-format {
        margin: 0 !important;
        padding: 0 !important;
    }

    .print-container {
        margin: 10mm 0 !important;
        font-family: Arial, sans-serif;
        font-size: 12px;
    }

    table {
        width: 100%;
        border-collapse: collapse;
    }

    th, td {
        border: 1px solid #000;
        padding: 5px;
    }

    th {
        font-weight: bold;
    }
}
