css:
.print-format { 
    width: 4in; 
    padding: 0.05in; 
    min-height: 8in; 
}
.items-table {
	width: 100%;
	font-size: 12px !important;
	border-collapse: collapse;
	margin: 2px 0;
}
.items-table th,
.items-table td {
	font-size: 12px !important;
	padding: 1px 2px;
	vertical-align: top;
	word-wrap: break-word;
}
.items-table th {
	font-weight: bold;
	border-bottom: 1px solid #000;
}
.print-format td, 
.print-format th {
	font-size: 12px; /* Reduced from 14px */
	font-weight: bold;
	color: #000000;
	line-height: 1.2; /* Reduced from 1.3 */
	border: none;
	padding: 1px; /* Added padding control */
}
